'use strict';

// https://262.ecma-international.org/11.0/#sec-isunsignedelementtype

module.exports = function IsUnsignedElementType(type) {
	return type === 'Uint8'
		|| type === 'Uint8C'
		|| type === 'Uint16'
		|| type === 'Uint32'
		|| type === 'BigUint64';
};
